    --chart-calories: 16 88% 65%;
    --chart-protein: 340 82% 75%;
    --chart-carbs: 45 93% 70%;
    --chart-fat: 142 76% 60%;

    /* ç¾ä¸½çš„æ¸å˜ */
    --gradient-warm: linear-gradient(135deg, hsl(16 88% 65%) 0%, hsl(340 82% 75%) 100%);
    --gradient-fresh: linear-gradient(135deg, hsl(142 76% 60%) 0%, hsl(180 76% 60%) 100%);
    --gradient-bg: linear-gradient(180deg, hsl(30 60% 98%) 0%, hsl(16 40% 96%) 100%);

    /* æŸ”å’Œçš„é˜´å½± */
    --shadow-sm: 0 2px 8px hsla(16 88% 65% / 0.1);
    --shadow-md: 0 4px 16px hsla(16 88% 65% / 0.15);
    --shadow-lg: 0 8px 24px hsla(16 88% 65% / 0.2);
    --shadow-colored: 0 8px 24px hsla(16 88% 65% / 0.25);

    /* å¹³æ»‘åŠ¨ç”» */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
