    --accent: 340 82% 65%;
    --accent-foreground: 0 0% 100%;

    --muted: 20 25% 15%;
    --muted-foreground: 30 20% 60%;

    --destructive: 0 84% 50%;
    --destructive-foreground: 0 0% 100%;

    --border: 20 25% 18%;
    --input: 20 25% 18%;
    --ring: 16 88% 65%;

    --gradient-bg: linear-gradient(180deg, hsl(20 30% 8%) 0%, hsl(20 30% 10%) 100%);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    background: var(--gradient-bg);
    background-attachment: fixed;
