export type MealType = 'æ—©é¤' | 'åˆé¤' | 'æ™šé¤' | 'åŠ é¤';

export interface Nutrition {
  calories: number;
  protein: number;
  carbs: number;
  fat: number;
}

export interface FoodEntry {
  id: string;
  name: string;
  mealType: MealType;
  portion: string;
  nutrition: Nutrition;
  photo?: string;
  timestamp: number;
  date: string; // YYYY-MM-DDæ ¼å¼
}

export interface DailyGoal {
  calories: number;
  protein: number;
  carbs: number;
  fat: number;
}

export interface UserProfile {
  dailyGoal: DailyGoal;
  preferences: string[];
}

import { FoodEntry, UserProfile } from '@/types';

const STORAGE_KEYS = {
  FOOD_ENTRIES: 'foodie-tracker-entries',
  USER_PROFILE: 'foodie-tracker-profile',
};

export const storageUtils = {
  // é£Ÿç‰©è®°å½•
  getFoodEntries(): FoodEntry[] {
    const data = localStorage.getItem(STORAGE_KEYS.FOOD_ENTRIES);
    return data ? JSON.parse(data) : [];
  },

  saveFoodEntry(entry: FoodEntry): void {
    const entries = this.getFoodEntries();
    entries.push(entry);
    localStorage.setItem(STORAGE_KEYS.FOOD_ENTRIES, JSON.stringify(entries));
  },

  deleteFoodEntry(id: string): void {
    const entries = this.getFoodEntries();
    const filtered = entries.filter((e) => e.id !== id);
    localStorage.setItem(STORAGE_KEYS.FOOD_ENTRIES, JSON.stringify(filtered));
  },

  getFoodEntriesByDate(date: string): FoodEntry[] {
    return this.getFoodEntries().filter((e) => e.date === date);
  },

  // ç”¨æˆ·é…ç½®
  getUserProfile(): UserProfile | null {
    const data = localStorage.getItem(STORAGE_KEYS.USER_PROFILE);
    return data ? JSON.parse(data) : null;
  },

  saveUserProfile(profile: UserProfile): void {
    localStorage.setItem(STORAGE_KEYS.USER_PROFILE, JSON.stringify(profile));
  },

  // è·å–é»˜è®¤ç›®æ ‡
  getDefaultProfile(): UserProfile {
    return {
      dailyGoal: {
        calories: 2000,
        protein: 50,
        carbs: 250,
        fat: 70,
      },
      preferences: [],
    };
  },
};

export const formatDate = (date: Date): string => {
  return date.toISOString().split('T')[0];
};

export const formatDisplayDate = (dateStr: string): string => {
  const date = new Date(dateStr);
  const today = new Date();
  const yesterday = new Date(today);
  yesterday.setDate(yesterday.getDate() - 1);

  if (formatDate(date) === formatDate(today)) {
    return 'ä»Šå¤©';
  } else if (formatDate(date) === formatDate(yesterday)) {
    return 'æ˜¨å¤©';
  } else {
    return `${date.getMonth() + 1}æœˆ${date.getDate()}æ—¥`;
  }
};

export const formatTime = (timestamp: number): string => {
  const date = new Date(timestamp);
  const hours = date.getHours().toString().padStart(2, '0');
  const minutes = date.getMinutes().toString().padStart(2, '0');
  return `${hours}:${minutes}`;
};

export const getTodayString = (): string => {
  return formatDate(new Date());
};

interface NutritionCircleProps {
  label: string;
  current: number;
  goal: number;
  color: string;
  unit?: string;
}

export const NutritionCircle = ({ label, current, goal, color, unit = 'g' }: NutritionCircleProps) => {
  const percentage = Math.min((current / goal) * 100, 100);
  const circumference = 2 * Math.PI * 45;
  const strokeDashoffset = circumference - (percentage / 100) * circumference;

  return (
    <div className="flex flex-col items-center gap-2 animate-slide-up">
      <div className="relative w-24 h-24">
        <svg className="transform -rotate-90 w-24 h-24">
          <circle
            cx="48"
            cy="48"
            r="45"
            stroke="currentColor"
            strokeWidth="8"
            fill="none"
            className="text-muted"
          />
          <circle
            cx="48"
            cy="48"
            r="45"
            stroke={color}
            strokeWidth="8"
            fill="none"
            strokeDasharray={circumference}
            strokeDashoffset={strokeDashoffset}
            className="transition-all duration-1000 ease-out"
            strokeLinecap="round"
          />
        </svg>
        <div className="absolute inset-0 flex flex-col items-center justify-center">
          <span className="text-lg font-bold text-foreground">{Math.round(current)}</span>
          <span className="text-xs text-muted-foreground">/{goal}</span>
        </div>
      </div>
      <div className="text-center">
        <p className="text-sm font-medium text-foreground">{label}</p>
        <p className="text-xs text-muted-foreground">{unit}</p>
      </div>
    </div>
  );
};

